mapper.select:
select bsn.* from t_business_serial_number bsn join t_business bs on bs.business_id=bsn.business_id WHERE 1=1 and bsn.`status`=true and bs.`status`=true
and bs.business_code=#{businessCode}
and bsn.business_sn_code=#{businessSnCode}

mapper.update:
update t_business_serial_number set status=false 
where use_status!='SUCCESS' and business_serial_number_id in
(

	#{item.businessSerialNumberId}

)

mapper.select:
select DATE_FORMAT(bsn.create_time,'%Y-%m-%d %H:%i:%s') create_time, DATE_FORMAT(bsn.update_time,'%Y-%m-%d %H:%i:%s') update_time, ch.channel_name, ap.app_name, bs.business_name, bsn.business_serial_number_id, bsn.business_sn_code, bsn.call_type, bsn.use_status, bsn.use_number, bsn.max_use_number, bsn.remark from t_business_serial_number bsn left join t_business bs on bs.business_id=bsn.business_id left join t_device_app_business_sn_code dasn on dasn.business_serial_number_id=bsn.business_serial_number_id and dasn.use_status=true left join t_device_app da on da.device_app_id=dasn.device_app_id left join t_app ap on ap.app_id=da.app_id left join t_channel ch on ch.status=true and ch.channel_id=ap.channel_id WHERE 1=1 and bsn.`status`=true and bs.`status`=true
and ch.channel_id=#{channelId}
and ap.app_id=#{appId}
and bs.business_id=#{businessId}
and bsn.business_sn_code like CONCAT('%',#{businessSnCode},'%')
and bsn.use_status=#{useStatus}
and DATE_FORMAT(bsn.create_time,'%Y%m%d')>=DATE_FORMAT(#{startTime},'%Y%m%d')
and DATE_FORMAT(bsn.create_time,'%Y%m%d')<=DATE_FORMAT(#{endTime},'%Y%m%d')
and bsn.remark like CONCAT('%',#{remark},'%')
GROUP BY bsn.business_serial_number_id order by bsn.update_time desc
