mapper.select:
select
	da.*
from t_device_app da
	join t_app ap on ap.app_id=da.app_id
where 1=1

	and da.device_id=#{deviceId}


	and da.app_id=#{appId}


	and ap.app_name=#{appName}			


	and ap.package_name=#{packageName}

select da.app_id, ap.app_name, da.sn_code, DATE_FORMAT(da.create_time,'%Y-%m-%d %H:%i:%s') createTime, DATE_FORMAT(da.activate_time,'%Y-%m-%d %H:%i:%s') activate_time from t_device_app da join t_app ap on ap.app_id=da.app_id where 1=1
and ap.channel_id=#{channelId}
and da.device_id=#{deviceId}
