mapper.select:
select ap.app_name name,count(d.device_id) value from t_device d join t_device_app da on da.device_id=d.device_id join t_app ap on ap.app_id=da.app_id where d.`status`=true and ap.`status`=true
and ap.channel_id=#{channelId}
GROUP BY ap.app_id ORDER BY count(d.device_id) desc limit 0,10

select ap.app_name name,count(d.device_id) value from t_device d join t_device_app da on da.device_id=d.device_id join t_app ap on ap.app_id=da.app_id where d.`status`=true and ap.`status`=true and !ISNULL(da.activate_time)
and ap.channel_id=#{channelId}
GROUP BY ap.app_id ORDER BY count(d.device_id) desc limit 0,10

mapper.update:
